import React,{Fragment} from 'react'
import {inject,observer} from 'mobx-react'
@inject('store')
@observer
export default class Mobx extends React.Component {
  constructor(props)  {
    super(props)
    console.log("Mobx_props:",props)
    props.store.home.increment = props.store.count.increment.bind( this )//this丢失的解决

  }

  increment = ()=>{
    console.log('mine_mine_mine');
    this.props.store.home.increment()
  }


  render() {
    return <Fragment>
      <button onClick={this.increment}>点我+</button>
      <p>count:{this.props.store.count.count}</p>
    </Fragment>
  }
}
