import React, { createRef } from 'react'
import './login.scss'
import {Button,Form,Input} from 'antd'

const Item = Form.Item
const layout = {
  labelCol: {
    span: 8,
  },
  wrapperCol: {
    span: 16,
  },
};
class Login extends React.Component {
  formRef = React.createRef();
  constructor(props) {
    super(props)
  }
  componentDidMount() {

  }
  loginAction = async ()=>{
    console.log('this.formRef:',this.formRef.current,)
    // const values = await this.formRef.current.validateFields();
    // console.log('loginAction:',values)
  }

  render() {
    return <div className='myLogin'>
      <Form {...layout} ref={this.formRef} >
        <Item label='账号' rules={[{required:true,message:'请输入账号',}]}>
          <Input placeholder='请输入账号' maxLength={24}></Input>
        </Item>
        <Item label='密码' rules={[{required:true,message:'请输入账号',}]}>
          <Input placeholder='请输入密码' type='password' maxLength={24}></Input>
        </Item>
      </Form>
      <h3>这是登录页面哈</h3>
      <Button type='primary' onClick={this.loginAction}>登录</Button>

    </div>
  }

}
export default Login
