import {observable,computed,action,makeObservable} from 'mobx'
class Home {

  constructor() {
    // 不添加观察,则相应的组件无法拿到更新后的数据
    makeObservable(this);
    this.testAdd = this.testAdd.bind(this);
  }

  @observable
    age = 18

  @computed
    get doubleAge() {
      return this.age * 2
    }

  @action 
    increment() {
      this.age ++;
      console.log('this.props.store.home.age:',this.age)

      //数据请求
      // fetch('/data/data.json')
      // .then(res => res.json())
      // .then( result => console.log( result ))
      // .catch( error => console.log( error )
    }

    delayIncrement(delay = 1000) {
      // 延迟++
      console.log("delayIncrement:",delay / 1000 + 's')
      setTimeout(() => {
        // this.age ++;
        this.increment()
      }, delay);
    }

    testAdd() {
      console.log('testAdd')
      // return new Promise((resolve,reject)=>{
      //   setTimeout(() => {
      //     resolve(100)
      //   }, 1500);
      //  })
    }

    async delaySyncNetwork() {
      this.testAdd();
      console.log('delaySyncNetwork_begin:',this)
      const response = await new Promise((resolve)=>{
        setTimeout(() => {
          resolve(500)
        }, 1000);
      })
      console.log('response:',response)
    } 





}
const home = new Home()
export default home
