import React from "react";
import { DesktopOutlined,FileOutlined,PieChartOutlined,TeamOutlined,UserOutlined,} from '@ant-design/icons';
import { Breadcrumb, Layout, Menu, theme } from 'antd';
import { Outlet,Link } from 'react-router-dom'


const { Header, Content, Footer, Sider } = Layout;

const URLMaps = {
  '/main/optionsone':'1',
  '/main/optionstwo':'2',
  "/main/content":'9',
}

class  MainLayout extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      collapsed:false,
    }
  }

  componentDidUpdate(preProps) {
    console.log('11111')
    console.log('preProps:',preProps.location,'props:',this.props.location)
  }

  componentWillMount(props) {
    console.log('componentWillMount')
  }

  componentWillReceiveProps(props) {
    console.log('componentWillReceiveProps')
  }

  componentDidMount() {
    const path = window.location.pathname;
    if (URLMaps[path]) {

    }
    console.log('componentDidMount:',path);
  }


  getItem(label, key, icon, children) {
    return {
      key,
      icon,
      children,
      label,
    };
  }
  // { path: "/main/content", element: <MainLayoutComponent /> },
  // { path: "/main/optionsone", element: <OptionsOne /> },
  // { path: "/main/optionstwo", element: <OptionsTWO /> },

  items = [
    this.getItem('Option 1', '1', <Link to={'/main/optionsone'}><PieChartOutlined /></Link>),
    this.getItem('Option 2', '2', <Link to={'/main/optionstwo'}><DesktopOutlined /></Link>),
    this.getItem('User', 'sub1', <UserOutlined />, [
      this.getItem('Tom', '3'),
      this.getItem('Bill', '4'),
      this.getItem('Alex', '5'),
    ]),
    this.getItem('Team', 'sub2', <TeamOutlined />, [this.getItem('Team 1', '6'), this.getItem('Team 2', '8')]),
    this.getItem('Files', '9', <Link to={"/main/content"}><FileOutlined /></Link>),
  ];



  render() {
    const {collapsed} = this.state
    return <Layout style={{minHeight:'100vh'}}>
      <Sider collapsible collapsed={collapsed} onCollapse={(value)=>{this.setState({collapsed:value})}}>
        <div className="demo-logo-vertical" />
        <Menu theme="dark" defaultSelectedKeys={['1']} mode="inline" items={this.items} />
      </Sider>
      <Layout>
        <Header
            style={{
              padding: 0,
              background: '$subFontColor',
            }}
        />
        <Content style={{margin:'0 16px'}}>
          <Breadcrumb style={{margin: '16px 0'}}>
            <Breadcrumb.Item>User</Breadcrumb.Item>
            <Breadcrumb.Item>Bill</Breadcrumb.Item>
          </Breadcrumb>
          <div style={{padding:24,minHeight:360,background:'$baseBackColor'}}>
            <Outlet/>
          </div>
        <Footer
          style={{
            textAlign: 'center',
          }}
        >
          zhangjunjun Â©2023 Created by zhangjunjun
        </Footer>
        </Content>

      </Layout>


    </Layout>
  }

}

export default MainLayout;
